// Application from Example 2-7 modi ed to be a module object 
"use strict";

const util = require('util');
const EventEmitter = require('events').EventEmitter;
const fs = require('fs');

class InputChecker extends EventEmitter {
  constructor(name, file) {
    super();

    this.name = name;
    this.writeStream = fs.createWriteStream('./' + file + '.txt', {
      'flags': 'a',
      'encoding': 'utf8',
      'mode': 0o666
    });
  }

  check(input) {
    // trim extraneous white space
    let command = input.toString().trim().substr(0, 3);
    // process command
    // if wr, write input to file 
    if (command == 'wr:') {
      this.emit('write', input.substr(3, input.length));
      // if en, end process
    } else if (command == 'en:') {
      this.emit('end');
      // just echo back to standard output
    } else {
      this.emit('echo', input);
    }
  }
}

exports.InputChecker = InputChecker;